import React, { Component } from 'react';
import Car from "./Car/Car";
class App extends Component {

  state = {
    cars: [
      {name: 'Ford', year: 2018},
      {name: 'Audi', year: 2012},
      {name: 'BMW', year: 2020},
    ],
    pageTitle: 'My cars'
  };

  handleClick = (pageTitle) => {
    this.setState({pageTitle});
  };

  render() {

    const divStyle = {
      textAlign: 'center'
    };

    return (
        <div className="App" style={divStyle}>
          <h2 style={{color: 'blue'}}>{this.state.pageTitle}</h2>

          <button onClick={this.handleClick.bind(this, 'Changed')}>Change title</button>
          {
            this.state.cars.map((car, index) => {
              return (
                  <Car name={car.name} year={car.year} key={index} onClick={() => {this.handleClick(car.name)}}/>
              );
            })
          }
        </div>
    )
  }
}

export default App;
