import React, { Component } from 'react';
import Car from "./Car/Car";
class App extends Component {

  state = {
    cars: [
      {name: 'Ford', year: 2018},
      {name: 'Audi', year: 2012},
      {name: 'BMW', year: 2020},
    ],
    pageTitle: 'My cars',
    showCars: false
  };

  handleClick = pageTitle => {
    this.setState({pageTitle});
  };

  handleShowHide = () => {
    this.setState({
      showCars: !this.state.showCars
    })
  };

  render() {

    const divStyle = {
      textAlign: 'center'
    };

    let cars = null;

    if (this.state.showCars) {
       cars = this.state.cars.map((car, index) => {
        return (
            <Car
                name={car.name}
                year={car.year}
                key={index}
                onClick={() => {this.handleClick(car.name)}}
                onChangeName={() => this.onChangeName(car.name)}
            />
        );
      })
    }

    return (
        <div className="App" style={divStyle}>
          <h2 style={{color: 'blue'}}>{this.state.pageTitle}</h2>
          <button onClick={this.handleShowHide}>Show/Hide</button>
          <button onClick={this.handleClick.bind(this, 'Changed')}>Change title</button>
          { cars }
        </div>
    )
  }
}

export default App;
